public class 문제3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num2;   
	    double num1 = 10/3.0;  //3
	    // '몫' 출력   
	    num2 = 10%3;  //1
	    // '나머지' 출력
	   System.out.printf("몫: %.2f \n", num1);   
	   System.out.printf("나머지: %d \n", num2); 
	   // 나누기 ==> double 
	}

}
